}

hello()

def query(db:String, table:String, quote:String => String) : String = {
s"""SELECT
    *
FROM
    ${quote(db)}.${quote(table)}
"""
}

def compute(xs: Int) = {
  def twice(y: Int) = 2*y
  def inc(y: Int) = y+1
  twice(xs) + inc(xs)
}

println(compute(23))

def fn(x:Int) = {
    if (x > 5)
        println("Big Number")
    else
        println("Small Number")
}

fn(7)

def fn(something:Any) : String = {
    something match {
        case "Alice" => "Hello Alice"
        case "Bob" => "Hi Bob"
        case name:String => s"Hey, are you $name?"
        case 24 => "Is it Christmas?"
        case true => "No it's false"
        case n:Int if (n%2 == 0) => "Even Number"
        case n:Int if (n%2 == 1) => "Odd Number"
    }
}

var x = 12

while (x < 20) {
    println("x = " + x)
    x = x+1
}

var x = 12

do {
    println("x = " + x)
    x = x +1
} while (x < 20)

