def foreach(seq: Seq[Int], fn:(Int)=>Unit) : Unit = {
    if (!seq.isEmpty) {
        val head = list.head
        val tail = list.tail

        fn(head)
        foreach(tail, fn)
    }
}

def foreach(seq: Seq[Int], fn:(Int)=>Unit) : Unit = {
  seq match {
      case head :: tail =>
          fn(head)
          foreach(tail, fn)
      case Seq() => Unit
  }    
}

foreach(List(1, 9, 2, 8, 3, 7, 4), println)

def findStatusRec(code:Int, candidates:Seq[(Int,String,String)]) : (Int,String,String) = {
  candidates match {
      case head :: tail if head._1 == code =>
          head
      case head :: tail =>
          findStatusRec(code, tail)
      case Seq() => (-1,"","")
  }    
}

def findStatus(code:Int) : (Int,String,String) = {
