// Declare a class called "Point" with a constructor with two arguments
class Point(x: Int, y:Int) {
    // Define a move method which creates a new Point
    def move(dx:Int, dy:Int) = {
        new Point(x + dx, y + dy)
    }

    // Define a toString methods which provides a nice representation of a Point. Since toString is
    // already present in all classes (this is a JVM feature), we need to explicitly override the
    // existing method.
    override def toString = {
        "[x=" + x + ",y=" + y + "]"
    }
}

class Point(val x: Int, val y:Int) {
    def move(dx:Int, dy:Int) = {
        new Point(x + dx, y + dy)
    }

    override def toString = {
        "[x=" + x + ",y=" + y + "]"
    }
}

val p1 = new Point(3,4)
p1.x

class Location(override val x:Int, override val y:Int, val name:String) extends Point(x,y) {
    override def move(dx:Int, dy:Int) = {
        new Location(x + dx, y + dy, name)
    }

    override def toString = {
        "[x=" + x + ",y=" + y + ",name=" + name + "]"
    }
}

val l1 = new Location(3,4,"Home")
val l2 = l1.move(5,9)

trait Addressable {
    var city:String
    var street:String

    def getAddress = street + "\n" + city
}

object Location {
    def create(x:Int, y:Int, name:String) = new Location(x,y,name)

    def apply(x:Int, y:Int, name:String) = create(x,y,name)
}

// A case class always has a parameter list. The (in this case empty) class body is optional
case class Person(name:String, age:Int) { }

val person = Person("Alice", 23)

// Normally equality is defined as object identity. Therefore two distinct object instances are considered to be unequal, even if their values are all equal
val loc = Location(12,13,"Home")
loc == Location(12,13,"Home")

def fn(name:String) = {
    name match {
        case "Alice" => println("Hello Alice")
        case "Bob" => println("Hi Bob")
        case x:String => println("Someone else (" + x + ")")
    }
}

case class Person(name:String, age:Int)
case class Point(x:Int, y:Int)

def fn(value:AnyRef) = {
    value match {
        case Person("Alice", age) => println("It's Alice (age " + age + ")")
        case Person(name,age) => println("We have a Person called " + name + " of age " + age)
        case Point(x,y) => println("We have a point at (" + x + "," + y + ")")
    }
}

def fn(value:AnyRef) = {
    value match {
        case Person("Alice", age) if age < 10 => println("It's little Alice (age " + age + ")")
        case Person("Alice", age) if age > 50 => println("It's old Alice (age " + age + ")")
        case Person(name,age) => println("We have a Person called " + name + " of age " + age)
        case Point(x,y) => println("We have a point at (" + x + "," + y + ")")
    }
}
